import React from 'react';
import { Navigate } from "react-router-dom";
import { useAuth } from "./Auth";

export const RequireAuth = ({ children }) => {
    const auth = useAuth()
    if (!auth.user) {
        <Navigate />
    }

    return (
        children
    )
}
