import React, { useReducer } from "react";

const initialState = {
    firstCount: 0,
}
const reducer = (state, action) => {
    switch (action) {
        case "increment":
            return { firstCount: state.firstCount + 1 }
        case "decrement":
            return { firstCount: state.firstCount - 1 }
        case "reset":
            return initialState
        default:
            return state;
    }
}

const UseReducerComplex = () => {
    useReducer(reducer, initialState)
    return <div>app</div>
}

export default UseReducerComplex
