import React, { useState } from 'react'

function UseMemo() {
    const [countOne, setCountOne] = useState(0)
    const [countTwo, setCountTwo] = useState(0)
    const incrementCountOne = () => {
        setCountOne(countOne + 1)
    }
    const incrementCountTwo = () => {
        setCountTwo(countTwo + 1)
    }
    const reset = () => {
        setCountOne(0)
        setCountTwo(0)
    }
    const isEvenCountOne = () => {
        return countOne % 2 === 0;
    }
    const isEvenCountTwo = () => {
        return countTwo % 2 === 0;
    }
    return (
        <div>
            <button onClick={incrementCountOne}>count One - {countOne}</button>
            <div>{isEvenCountOne() ? "even" : "odd"}</div>
            <button onClick={incrementCountTwo}>count Two - {countTwo}</button>
            <div>{isEvenCountTwo() ? "even" : "odd"}</div>
            <button onClick={reset}>reset</button>
        </div>
    )
}

export default UseMemo
