import { useState } from 'react';

const useInput = () => {
    const [value, setValue] = useState("");
    const reset = () => {
        setValue("");
    }
    const bind = {
        value,
        type: "text",
        onChange: (e) => setValue(e.target.value),
        placeholder: value
    }
    return [value, bind, reset]
}

export default useInput
